import fs from 'fs';
import Module from './Module';


function padLeft(str, value, length) {
  str = str + '';
  while (str.length < length) { str = value + str; }
  return str;
}

/**
 * @private
 */
export default class Survey extends Module {
  constructor(options = {}) {
    super(options.name ||Â 'survey');
    // prepare file name
    const now = new Date();
    const year = padLeft(now.getFullYear(), 0, 2);
    const month = padLeft(now.getMonth() + 1, 0, 2);
    const day = padLeft(now.getDate(), 0, 2);
    const hour = padLeft(now.getHours(), 0, 2);
    const minutes = padLeft(now.getMinutes(), 0, 2);
    const fileName = `survey-${year}-${month}-${day}_${hour}-${minutes}`;

    // @TODO allow to change folder name (aka `surveys`)
    this.filePath = `${process.cwd()}/surveys/${fileName}`;
    this._appendToFile = this._appendToFile.bind(this);
  }

  connect(client) {
    super.connect(client);

    client.receive(`${this.name}:answers`, this._appendToFile)
  }

  disconnect(client) {
    super.disconnect(client);
  }

  _appendToFile(json) {
    fs.appendFile(this.filePath, `${json}\n`, (err) => {
      if (err) { console.error(err.message); }
      console.log(json + ' appended to ' + this.filePath);
    });
  }
}

import SyncServer from 'sync/server';
import Module from './Module';


/**
 * [server] Synchronize the local clock on a master clock shared by the server and the clients.
 *
 * Both the clients and the server can use this master clock as a common time reference.
 * For instance, this allows all the clients to do something exactly at the same time, such as blinking the screen or playing a sound in a synchronized manner.
 *
 * **Note:** the module is based on [`github.com/collective-soundworks/sync`](https://github.com/collective-soundworks/sync).
 *
 * (See also {@link src/client/Sync.js~Sync} on the client side.)
 *
 * @example const sync = new Sync();
 *
 * const nowSync = sync.getSyncTime(); // current time in the sync clock time
 */
export default class Sync extends Module {
  /**
   * Creates an instance of the class.
   * @param {Object} [options={}] Options.
   * @param {String} [name='sync'] Name of the module.
   */
  constructor(options = {}) {
    super(options.name || 'sync');

    this._hrtimeStart = process.hrtime();

    this._sync = new SyncServer(() => {
      const time = process.hrtime(this._hrtimeStart);
      return time[0] + time[1] * 1e-9;
    });
  }

  /**
   * @private
   * @todo ?
   */
  connect(client) {
    super.connect(client);
    this._sync.start((cmd, ...args) => client.send(cmd, ...args), (cmd, callback) => client.receive(cmd, callback));
  }

  /**
   * Returns the current time in the sync clock.
   * @return {Number} Current sync time (in seconds).
   */
  getSyncTime() {
    return this._sync.getSyncTime();
  }
}

