'use strict';

const Calibration = require('calibration/client');

import client from './client';
import ClientModule from './ClientModule.es6.js';

export default class ClientCalibration extends ClientModule {
  /**
   * Function called when an update happened.
   *
   * See {@linkcode ClientCalibration~load}.
   *
   * @callback ClientCalibration~updateFunction
   **/

  /**
   * Constructor of the calibration client module.
   *
   * Note that {@linkcode ClientCalibration~start} method must be
   * called to restore a previous calibration.
   *
   * @constructs ClientCalibration
   * @param {Object} [params]
   * @param {String} [params.name='calibration'] name of module
   * @param {String} [params.color='black'] background
   * @param {ClientCalibration~updateFunction} [params.updateFunction]
   * Called whenever the calibration changed. First to complete the
   * start, by calling done, and then each time the calibration is
   * restored from the server, because this is asynchronous.
   */
  constructor(params = {}) {
    super(params.name || 'calibration', true, params.color || 'black');
    const that = this;

    this.ready = false;
    this.started = false;

    // undefined is fine
    this.updateFunction = params.updateFunction;

    this.calibration = new Calibration({
      sendFunction: client.send,
      receiveFunction: client.receive,
      updateFunction: () => { that._calibrationUpdated(); }
    });

    this.setCenteredViewContent('<p class="soft-blink">Calibration, stand by…</p>');
  }

  /**
   * Register the receive functions, and restore the calibration from
   * local storage, or from the server.
   *
   * @function ClientCalibration~start
   */
  start() {
    super.start();
    // load previous calibration on start.
    this.load();
    // done when actually loaded
  }

  /**
   * Save calibration locally, and on the server.
   *
   * @function ClientCalibration~save
   */
  save() {
    this.calibration.save();
  }

  /**
   * Load calibration locally, or from the server.
   *
   * The calibration is loaded from the server when no local
   * configuration is found. Note that loading from the server is
   * asynchronous. See {@linkcode ClientCalibration~updateFunction}
   * passed to the constructor.
   *
   * @function ClientCalibration~load
   */
  load() {
    this.calibration.load();
  }

  /**
   * Locally set the calibrated values.
   *
   * @function ClientCalibration~set
   * @param {calibration} params
   */
  set(params) {
    this.calibration.set(params);
  }

  /**
   * Locally get the calibrated values.
   *
   * Note that {@linkcode CalibrationClient~load} method must be
   * called to restore a previous calibration.
   *
   * @function ClientCalibration~get
   * @returns {calibration} or the empty object {} if no calibration
   * is available.
   */
  get() {
    return this.calibration.get();
  }

  _calibrationUpdated() {
    if(!this.started) {
      this.started = true;
      this.done();
    }
    if(typeof this.updateFunction !== 'undefined') {
      this.updateFunction();
    }
  }
}

'use strict';

const input = require('./input');

import client from './client.es6.js';
import ClientModule from './ClientModule.es6.js';

function _instructions(label) {
  return "<p>Go to</p>" +
    "<div class='checkin-label circled'><span>" + label + "</span></div>" +
    "<p><small>Touch the screen<br/>when you are ready.</small></p>";
}

/**
 * The `Checkin` module is responsible for keeping track of the connected clients by assigning them indices.
 * In case that the scenario is based on predefined positions (*i.e.* a `setup`), the indices correspond to the indices of positions that can be either assigned automatically or selected by the participants.
 *
 * For instance, say that the scenario requires 12 participants who sit on a grid of 3 ⨉ 4 predefined positions.
 * When a client connects to the server, the `Checkin` module could assign the client to a position on the grid that is not occupied yet.
 * The application can indicate the participant a label that is associated with the assigned position.
 * Similarly, if the scenario takes place in a theater with labeled seats, the `Checkin` module would allow the participants to indicate their seat by its label (*e.g.* a row and a number).
 * If the scenario does not require the participants to sit at particular locations, the `Checkin` module would just assign them arbitrary indices within the range of total number of users the scenario supports.
 *
 * Alternatively, when configuring the module adequately, the module can assign arbitrary indices to the the participants and request that they indicate their approximate location in the performance space on a map.
 *
 * The {@link ClientCheckin} module takes care of the check-in on the client side.
 * The {@link ClientCheckin} module calls its `done` method when the user is checked in.
 *
 * The {@link ClientCheckin} module requires the SASS partial `_77-checkin.scss`.
 */
export default class ClientCheckin extends ClientModule {
  /**
   * Creates an instance of the class. Always has a view.
   * @param {Object} [options={}] Options.
   * @param {String} [options.name='checkin'] Name of the module.
   * @param {Boolean} [options.hasView=true] Indicates whether the module has a view or not.
   * @param {String} [options.color='black'] Background color of the `view`.
   * @param {Boolean} [options.showDialog=false] Indicates whether the view displays text or not.
   * @param {Function(label:String) : String} [options.instructions] Function to display the instructions.
   * @todo default `instructions` value
   */
  constructor(options = {}) {
    super(options.name || 'checkin', options.hasView || true, options.color);

    this.index = -1;
    this.label = null;

    this._showDialog = options.showDialog || false;
    this._instructions = options.instructions || _instructions;

    this._acknowledgementHandler = this._acknowledgementHandler.bind(this);
    this._unavailableHandler = this._unavailableHandler.bind(this);
    this._viewClickHandler = this._viewClickHandler.bind(this);
  }

  /**
   * Starts the module.
   * Sends a request to the server and sets up listeners for the server's response.
   */
  start() {
    super.start();

    client.send(this.name + ':request');

    client.receive(this.name + ':acknowledge', this._acknowledgementHandler);
    client.receive(this.name + ':unavailable', this._unavailableHandler);
  }

  /**
   * Resets the module to default state.
   * Removes WebSocket and click / touch listeners.
   */
  reset() {
    super.reset();

    client.removeListener(this.name + ':acknowledge', this._acknowledgementHandler);
    client.removeListener(this.name + ':unavailable', this._unavailableHandler);

    if (client.platform.isMobile)
      this.view.removeEventListener('touchstart', this._viewClickHandler);
    else
      this.view.removeEventListener('click', this._viewClickHandler, false);
  }

  /**
   * Restarts the module.
   * Sends the index, label and coordinates to the server.
   */
  restart() {
    super.restart();

    client.send(this.name + ':restart', this.index, this.label, client.coordinates);
    this.done();
  }

  _acknowledgementHandler(index, label, coordinates) {
    this.index = index;

    if (coordinates)
      client.coordinates = coordinates;

    if (label) {
      this.label = label;

      if (this._showDialog) {
        let htmlContent = this._instructions(label);
        this.setCenteredViewContent(htmlContent);

        if (client.platform.isMobile)
          this.view.addEventListener('touchstart', this._viewClickHandler);
        else
          this.view.addEventListener('click', this._viewClickHandler, false);
      } else {
        this.done();
      }

    } else {
      this.done();
    }
  }

  _unavailableHandler() {
    this.setCenteredViewContent("<p>Sorry, we cannot accept any more connections at the moment, please try again later.</p>");
  }

  _viewClickHandler() {
    this.done();
  }
}

module.exports = ClientCheckin;

'use strict';

import client from './client.es6.js';
import ClientModule from './ClientModule.es6.js';

class ControlEvent {
  constructor(type, name, label) {
    this.type = type;
    this.name = name;
    this.label = label;
    this.value = undefined;
  }

  set(val) {

  }

  send() {
    client.send('control:event', this.name, this.value);
  }
}

class ControlNumber extends ControlEvent {
  constructor(init, view = null) {
    super('number', init.name, init.label);
    this.min = init.min;
    this.max = init.max;
    this.step = init.step;
    this.box = null;

    if (view) {
      let box = this.box = document.createElement('input');
      box.setAttribute('id', this.name + '-box');
      box.setAttribute('type', 'number');
      box.setAttribute('min', this.min);
      box.setAttribute('max', this.max);
      box.setAttribute('step', this.step);
      box.setAttribute('size', 16);

      box.onchange = (() => {
        let val = Number(box.value);
        this.set(val);
        this.send();
      });

      let incrButton = document.createElement('button');
      incrButton.setAttribute('id', this.name + '-incr');
      incrButton.setAttribute('width', '0.5em');
      incrButton.innerHTML = '>';
      incrButton.onclick = (() => {
        this.incr();
        this.send();
      });

      let decrButton = document.createElement('button');
      decrButton.setAttribute('id', this.name + '-descr');
      decrButton.style.width = '0.5em';
      decrButton.innerHTML = '<';
      decrButton.onclick = (() => {
        this.decr();
        this.send();
      });

      let label = document.createElement('span');
      label.innerHTML = this.label + ': ';

      let div = document.createElement('div');
      div.appendChild(label);
      div.appendChild(decrButton);
      div.appendChild(box);
      div.appendChild(incrButton);
      div.appendChild(document.createElement('br'));

      view.appendChild(div);
    }

    this.set(init.value);
  }

  set(val, send = false) {
    this.value = Math.min(this.max, Math.max(this.min, val));

    if (this.box)
      this.box.value = val;
  }

  incr() {
    let steps = Math.floor(this.value / this.step + 0.5);
    this.set(this.step * (steps + 1));
  }

  decr() {
    let steps = Math.floor(this.value / this.step + 0.5);
    this.set(this.step * (steps - 1));
  }
}

class ControlSelect extends ControlEvent {
  constructor(init, view = null) {
    super('select', init.name, init.label);
    this.options = init.options;
    this.box = null;

    if (view) {
      let box = this.box = document.createElement('select');
      box.setAttribute('id', this.name + '-box');

      for (let option of this.options) {
        let optElem = document.createElement("option");
        optElem.value = option;
        optElem.text = option;
        box.appendChild(optElem);
      }

      box.onchange = (() => {
        this.set(box.value);
        this.send();
      });

      let incrButton = document.createElement('button');
      incrButton.setAttribute('id', this.name + '-incr');
      incrButton.setAttribute('width', '0.5em');
      incrButton.innerHTML = '>';
      incrButton.onclick = (() => {
        this.incr();
        this.send();
      });

      let decrButton = document.createElement('button');
      decrButton.setAttribute('id', this.name + '-descr');
      decrButton.style.width = '0.5em';
      decrButton.innerHTML = '<';
      decrButton.onclick = (() => {
        this.decr();
        this.send();
      });

      let label = document.createElement('span');
      label.innerHTML = this.label + ': ';

      let div = document.createElement('div');
      div.appendChild(label);
      div.appendChild(decrButton);
      div.appendChild(box);
      div.appendChild(incrButton);
      div.appendChild(document.createElement('br'));

      view.appendChild(div);
    }

    this.set(init.value);
  }

  set(val, send = false) {
    let index = this.options.indexOf(val);

    if (index >= 0) {
      this.value = val;
      this.index = index;

      if (this.box)
        this.box.value = val;
    }
  }

  incr() {
    this.index = (this.index + 1) % this.options.length;
    this.set(this.options[this.index]);
  }

  decr() {
    this.index = (this.index + this.options.length - 1) % this.options.length;
    this.set(this.options[this.index]);
  }
}

class ControlInfo extends ControlEvent {
  constructor(init, view = null) {
    super('info', init.name, init.label);
    this.box = null;

    if (view) {
      let box = this.box = document.createElement('span');
      box.setAttribute('id', this.name + '-box');

      let label = document.createElement('span');
      label.innerHTML = this.label + ': ';

      let div = document.createElement('div');
      div.appendChild(label);
      div.appendChild(box);
      div.appendChild(document.createElement('br'));

      view.appendChild(div);
    }

    this.set(init.value);
  }

  set(val) {
    this.value = val;

    if (this.box)
      this.box.innerHTML = val;
  }
}

class ControlCommand extends ControlEvent {
  constructor(init, view = null) {
    super('command', init.name, init.label);

    if (view) {
      let div = document.createElement('div');
      div.setAttribute('id', this.name + '-btn');
      div.classList.add('command');
      div.innerHTML = this.label;

      div.onclick = (() => {
        this.send();
      });

      view.appendChild(div);
      view.appendChild(document.createElement('br'));
    }
  }
}

/**
 * The {@link ClientControl} module takes care of the global `parameters`, `infos`, and `commands` on the client side.
 * If the module is instantiated with the `gui` option set to `true`, it constructs the graphical control interface.
 * Otherwise it simply receives the values that are emitted by the server (usually by through the `performance` module).
 *
 * The {@link ClientControl} calls its `done` method:
 * - Immediately after having set up the controls if the GUI is disabled;
 * - Never if the GUI is enabled.
 */
export default class ClientControl extends ClientModule {
  /**
   * Creates an instance of the class.
   * @param {Object} [options={}] Options.
   * @param {String} [options.name='sync'] Name of the module.
   * @param {String} [options.color='black'] Background color of the `view`.
   * @param {Boolean} [options.gui=true] Indicates whether to create the graphical user interface to control the parameters or not.
   * @emits {'control:event'} when the server sends an update.
   */
  constructor(options = {}) {
    super(options.name || 'control', (options.gui === true), options.color);

    /**
     * Dictionary of all the parameters and commands.
     * @type {Object}
     */
    this.events = {};

    let view = hasGui ? this.view : null;

    client.receive('control:init', (events) => {
      if (view) {
        let title = document.createElement('h1');
        title.innerHTML = 'Conductor';
        view.appendChild(title);
      }

      for (let key of Object.keys(events)) {
        let event = events[key];

        switch (event.type) {
          case 'number':
            this.events[key] = new ControlNumber(event, view);
            break;

          case 'select':
            this.events[key] = new ControlSelect(event, view);
            break;

          case 'info':
            this.events[key] = new ControlInfo(event, view);
            break;

          case 'command':
            this.events[key] = new ControlCommand(event, view);
            break;
        }
      }

      if (!view)
        this.done();
    });

    // listen to events
    client.receive('control:event', (name, val) => {
      let event = this.events[name];

      if (event) {
        event.set(val);
        this.emit('control:event', name, val);
      }
      else
        console.log('client control: received unknown event "' + name + '"');
    });
  }

  /**
   * Starts the module and requests the parameters to the server.
   */
  start() {
    super.start();
    client.send('control:request');
  }

  /**
   * Restarts the module and requests the parameters to the server.
   */
  restart() {
    super.restart();
    client.send('control:request');
  }

  /**
   * Sends a value or command to the server.
   * @param {String} name Name of the parameter or command to send.
   * @todo is this method useful?
   */
  send(name) {
    const event = this.events[name];

    if (event) {
      event.send();
    }
  }

  /**
   * Updates the value of a parameter.
   * @param {String} name Name of the parameter to update.
   * @param {(String|Number|Boolean)} val New value of the parameter.
   */
  update(name, val) {
    const event = this.events[name];

    if (event) {
      event.set(val);
      event.send();
    }
  }
}

'use strict';

const audioContext = require('waves-audio').audioContext;

import client from './client.es6.js';
import ClientModule from './ClientModule.es6.js';

/**
 * @private
 */
function _base64(format, base64) {
  return `data:${format};base64,${base64}`;
}

/**
 * The {@link ClientDialog} displays a full screen dialog. It requires the participant to tap the screen to make the view disappear. The module is also used at the very beginning of a scenario to activate the Web Audio API on iOS devices (with the option `activateWebAudio`). The `ClientDialog` module calls its `done` method when the participant taps on the screen.
 * @example
 * const welcomeDialog = new ClientDialog({
 *   name: 'welcome',
 *   text: 'Welcome to this awesome scenario!',
 *   color: 'alizarin'
 * });
 */
export default class ClientDialog extends ClientModule {
  /**
   * Creates an instance of the class. Always has a view.
   * @param {Object} [options={}] Options.
   * @param {String} [options.name='dialog'] Name of the module.
   * @param {String} [options.color='black'] Background color of the `view`.
   * @param {String} [options.text='Hello!'] Text to be displayed in the `view`.
   * @param {Boolean} [options.activateAudio=false] Indicates whether the module activates the Web Audio API when the participant touches the screen (useful on iOS devices).
   * @param {Boolean} [options.wakeLock=false] Indicates whether the modules activates an ever-looping 1-pixel video to prevent the device from going idle.
   */
  constructor(options = {}) {
    super(options.name || 'dialog', true, options.color);

    this._mustActivateAudio = !!options.activateAudio;
    this._mustWakeLock = !!options.wakeLock;
    this._text = options.text || "Hello!";

    this._clickHandler = this._clickHandler.bind(this);
  }

  start() {
    super.start();

    // Display text
    this.setCenteredViewContent(this._text);

    // Initialize video element for wakeLocking
    this._initWakeLock();

    // Add click listnener
    this.view.addEventListener('click', this._clickHandler);
  }

  restart() {
    super.restart();
    this.done();
  }

  _activateAudio() {
    var o = audioContext.createOscillator();
    var g = audioContext.createGain();
    g.gain.value = 0;
    o.connect(g);
    g.connect(audioContext.destination);
    o.start(0);
    o.stop(audioContext.currentTime + 0.01);
  }

  _clickHandler() {
    if (this._mustActivateAudio)
      this._activateAudio();

    if (this._mustWakeLock)
      this._requestWakeLock();

    this.view.removeEventListener('click', this._clickHandler);

    this.done();
  }

  // cf. https://github.com/borismus/webvr-boilerplate/blob/8abbc74cfa5976b9ab0c388cb0c51944008c6989/js/webvr-manager.js#L268-L289
  _initWakeLock() {
    this._wakeLockVideo = document.createElement('video');

    this._wakeLockVideo.addEventListener('ended', () => {
      this._wakeLockVideo.play();
    });
  }

  _requestWakeLock() {
    const os = client.platform.os;
    this._releaseWakeClock();

    if (os === 'ios') {
      if (this._wakeLockTimer) return;

      this._wakeLockTimer = setInterval(() => {
        window.location = window.location;
        setTimeout(window.stop, 0);
      }, 30000);
    } else if (os === 'android') {
      if (this._wakeLockVideo.paused === false) return;

      this._wakeLockVideo.src = _base64('video/webm', '');
      this._wakeLockVideo.play();
    }
  }

  _releaseWakeClock() {
    const os = client.platform.os;

    if (os === 'ios') {
      if (this._wakeLockTimer) {
        clearInterval(this._wakeLockTimer);
        this._wakeLockTimer = null;
      }
    } else if (os === 'android') {
      this._wakeLockVideo.pause();
      this._wakeLockVideo.src = '';
    }
  }
}

