'use strict';

const log = require('./logger');

/**
 * The {@link ServerClient} module is used to keep track of each connected client and to communicate with it via WebSockets.
 * Each time a client of type `clientType` connects to the server, *Soundworks* creates a new instance of `ServerClient`.
 * An instance of the class is passed to the `connect` and `disconnect` methods of all the server side modules that are mapped to the `clientType` clients (see {@link server.map}), as well as to the `enter` and `exit` methods of any {@link ServerPerformance} class mapped to that same client type.
 */
export default class ServerClient {
	/**
	 * Creates an instance of the class.
	 * @param {String} clientType Client type of the connected client.
	 * @param {Socket} socket Socket object used to comminuate with the client.
	 */
	constructor(clientType, socket) {
		/**
		 * Client type (specified when initializing the {@link client} object on the client side with {@link client.init}).
		 * @type {String}
		 */
    this.type = clientType;

		/**
		 * Index of the client as set by the {@link ServerCheckin} module.
		 * @type {Number}
		 */
    this.index = -1;

		/**
		 * Coordinates of the client in the space, stored as an `[x, y]` array.
		 * @type {Number[]}
		 */
    this.coordinates = null;

		/**
		 * Used by any {@link ServerModule} to associate data to a particular client.
		 * All the data associated with a module whose `name` is `moduleName` is accessible through the key `moduleName`.
		 * For instance, the {@link ServerSync} module keeps track of the time offset between the client and the sync clocks in `this.modules.sync.timeOffset`.
		 * Similarly, a {@link ServerPerformance} module could keep track of each client's status in `this.modules.myPerformanceName.status`.
		 * @type {Object}
		 */
    this.modules = {};

		/**
		 * Socket used to communicate with the client.
		 * @type {Socket}
		 * @todo make private?
		 */
    this.socket = socket;
  }

	/**
	 * Sends a WebSocket message to the client.
	 * @param {String} msg Name of the message.
	 * @param {...*} args Arguments of the message (as many as needed, of any type).
	 */
  send(msg, ...args) {
    log.trace({ socket: this.socket, clientType: this.type, channel: msg, arguments: args }, 'socket.send');
    this.socket.emit(msg, ...args);
  }

	/**
	 * Sends a volatile WebSocket message to the client, *i.e.* a non-blocking WebSocket message.
	 * @param {String} msg Name of the message to send.
	 * @param {...*} args Arguments of the message (as many as needed, of any type).
	 */
  sendVolatile(msg, ...args) {
    log.trace({ socket: this.socket, clientType: this.type, channel: msg, arguments: args }, 'socket.sendVolatile');
    this.socket.volatile.emit(msg, ...args);
  }

	/**
	 * Executes a callback function when it receives a WebSocket message from the client.
	 * @param {String} msg Name of the received message.
	 * @param {Function} callback Callback function executed when the message is received.
	 */
  receive(msg, callback) {
    var _callback = (function(...args) {
      log.trace({ socket: this.socket, clientType: this.type, channel: msg, arguments: args }, 'socket.receive');
      callback.apply(this.socket, args);
    }).bind(this);

    this.socket.on(msg, _callback);
  }

	/**
	 * Sends a WebSocket message to all the other clients of the same type (*i.e.* all the clients of type `this.clientType`, excluding this client).
	 * @param {String} msg Name of the message to broadcast.
	 * @param {...*} args Arguments of the message (as many as needed, of any type).
	 */
  broadcast(msg, ...args) {
    log.trace({ socket: this.socket, clientType: this.type, channel: msg, arguments: args }, 'socket.broadcast');
    this.socket.broadcast.emit(msg, ...args);
  }
}

'use strict';

import server from './server.es6.js';
import ServerModule from './ServerModule.es6.js';

/**
 * The `Control` module is used to control an application through a dedicated client (that we usually call `conductor`).
 * The module allows for declaring `parameters`, `infos`, and `commands`, through which the `conductor` can control and monitor the state of the application:
 * - `parameters` are values that are changed by a client, sent to the server, and propagated to the other connected clients (*e.g.* the tempo of a musical application);
 * - `infos` are values that are changed by the server and propagated to the connected clients, to inform about the current state of the application (*e.g.* the number of connected `player` clients);
 * - `commands` are messages (without arguments) that are send from the client to the server (*e.g.* `start`, `stop` or `clear` — whatever they would mean for a given application) which does the necessary upon reception.
 *
 * Optionally, the {@link ClientControl} module can automatically construct a simple interface from the list of declared controls that allows to change `parameters`, display `infos`, and send `commands` to the server.
 *
 * The controls of different types are declared on the server side and propagated to the client side when a client is set up.
 *
 * The {@link ServerControl} takes care of the parameters and commands on the server side.
 * To set up controls in a scenario, you should extend this class on the server side and declare the controls specific to that scenario with the appropriate methods.
 * @example
 * class Control extends ServerControl {
 *   constructor(nameArray) {
 *     super();
 *
 *     // Info corresponding to the number of players
 *     this.addInfo('numPlayers', 'Number of players', 0);
 *     // Number parameter corresponding to the output gain of the players
 *     this.addNumber('gain', 'Global gain', 0, 1, 0.1, 1);
 *     // Select parameter corresponding to the state of the performance
 *     this.addSelect('state', 'State', ['idle', 'playing'], 'idle');
 *     // Command to reload the page on all the clients
 *     this.addCommand('reload', 'Reload the page of all the clients');
 *   }
 * }
 *
 * const control = new Control(guiroNames, soundfieldNames);
 */
export default class ServerControl extends ServerModule {
  /**
   * Creates an instance of the class.
   * @param {Object} [options={}] Options.
   * @param {String} [options.name='control'] Name of the module.
   */
  constructor(options = {}) {
    super(options.name || 'control');

    /**
     * Dictionary of all the events.
     * @type {Object}
     */
    this.events = {};

    this._clientTypes = [];
  }

  /**
   * Adds a number parameter.
   * @param {String} name Name of the parameter.
   * @param {String} label Label of the parameter (displayed on the control GUI on the client side).
   * @param {Number} min Minimum value of the parameter.
   * @param {Number} max Maximum value of the parameter.
   * @param {Number} step Step to increase or decrease the parameter value.
   * @param {Number} init Initial value of the parameter.
   * @param {String[]} [clientTypes=null] Array of the client types to send the parameter to. If not set, the parameter is sent to all the client types.
   */
  addNumber(name, label, min, max, step, init, clientTypes = null) {
    this.events[name] = {
      type: 'number',
      name: name,
      label: label,
      min: min,
      max: max,
      step: step,
      value: init,
      clientTypes: clientTypes
    };
  }

  /**
   * Adds a select parameter.
   * @param {String} name Name of the parameter.
   * @param {String} label Label of the parameter (displayed on the control GUI on the client side).
   * @param {String[]} options Array of the different values the parameter can take.
   * @param {Number} init Initial value of the parameter (has to be in the `options` array).
   * @param {String[]} [clientTypes=null] Array of the client types to send the parameter to. If not set, the parameter is sent to all the client types.
   */
  addSelect(name, label, options, init, clientTypes = null) {
    this.events[name] = {
      type: 'select',
      name: name,
      label: label,
      options: options,
      value: init,
      clientTypes: clientTypes
    };
  }

  /**
   * Adds an info parameter.
   * @param {String} name Name of the parameter.
   * @param {String} label Label of the parameter (displayed on the control GUI on the client side).
   * @param {Number} init Initial value of the parameter (has to be in the `options` array).
   * @param {String[]} [clientTypes=null] Array of the client types to send the parameter to. If not set, the parameter is sent to all the client types.
   */
  addInfo(name, label, init, clientTypes = null) {
    this.events[name] = {
      type: 'info',
      name: name,
      label: label,
      value: init,
      clientTypes: clientTypes
    };
  }

  /**
   * Adds a command.
   * @param {String} name Name of the command.
   * @param {String} label Label of the command (displayed on the control GUI on the client side).
   * @param {String[]} [clientTypes=null] Array of the client types to send the parameter to. If not set, the parameter is sent to all the client types.
   */
  addCommand(name, label, clientTypes = null) {
    this.events[name] = {
      type: 'command',
      name: name,
      label: label,
      value: undefined,
      clientTypes: clientTypes
    };
  }

  _broadcastEvent(event) {
    let clientTypes = event.clientTypes || this._clientTypes;

    // propagate parameter to clients
    for (let clientType of clientTypes)
      server.broadcast(clientType, 'control:event', event.name, event.value);

    this.emit('control:event', event.name, event.value);
  }

  /**
   * Sends an event to the clients.
   * @param {String} name Name of the event to send.
   * @emits 'control:event'
   */
  send(name) {
    let event = this.events[name];

    if (event) {
      this._broadcastEvent(event);
    } else {
      console.log('server control: send unknown event "' + name + '"');
    }
  }

  /**
   * Updates the value of a parameter and sends it to the clients.
   * @param {String} name Name of the parameter to update.
   * @param {(String|Number|Boolean)} value New value of the parameter.
   */
  update(name, value) {
    let event = this.events[name];

    if (event) {
      event.value = value;
      this._broadcastEvent(event);
    } else {
      console.log('server control: update unknown event "' + name + '"');
    }
  }

  connect(client) {
    super.connect(client);

    let clientType = client.type;

    if (this._clientTypes.indexOf(clientType) < 0)
      this._clientTypes.push(clientType);

    // init control parameters, infos, and commands at client
    client.receive('control:request', () => {
      client.send('control:init', this.events);
    });

    // listen to control parameters
    client.receive('control:event', (name, value) => {
      this.update(name, value);
    });
  }
}

