'use strict';

const fs = require('fs');

import ServerModule from './ServerModule.es6.js';

function checkForExtensions(file, extensions) {
  if(!extensions || extensions.length === 0)
    return true;

  for (let extension of extensions) {
    let extensionIndex = file.length - extension.length;

    if(extensionIndex >= 0 && file.substring(extensionIndex, extensionIndex + extension.length) === extension)
      return true;
  }

  return false;
}

/**
 * The {@link ServerFilelist} module allows to dynamically send a list of files stored on the server to the clients.
 */
export default class ServerFilelist extends ServerModule {
  /**
   * Creates an instance of the class.
   * @param {Object} [options={}] Options.
   * @param {String} [options.name='filelist'] Name of the module.
   */
  constructor(options = {}) {
    super(options.name || 'filelist');
  }

  /**
   * @private
   */
  connect(client) {
    super.connect(client);

    client.receive(this.name + ':request', (subfolder, extensions) => {
      let filesList = [];

      fs.readdir('./public/' + subfolder, (err, files) => {
        if (err) throw err;

        for (let file of files) {
          if (file[0] !== '.' && checkForExtensions(file, extensions))
            filesList.push(file);
        }

        client.send(this.name + ':files', filesList);
      });
    });
  }
}

module.exports = ServerFilelist;

'use strict';

import ServerModule from './ServerModule.es6.js';

/**
 * The {@link ServerLocator} module allows to store the coordinates of a client when the user enters an approximate location through the interfacte provided by the {@link ClientLocator}.
 */
export default class ServerLocator extends ServerModule {
  /**
   * Creates an instance of the class.
   * @param {Object} [options={}] Options.
   * @param {Object} [options.name='locator'] Name of the module.
   * @param {Object} [options.setup] Setup used in the scenario, if any (cf. {@link ServerSetup}).
   */
  constructor(options = {}) {
    super(options.name || 'locator');

    this.setup = options.setup || null;
  }

  /**
   * @private
   */
  connect(client) {
    super.connect(client);

    client.receive(this.name + ':request', () => {
      if (this.setup) {
        let surface = this.setup.getSurface();
        client.send(this.name + ':surface', surface);
      } else {
        throw new Error("Locator requires a setup.");
      }
    });

    client.receive(this.name + ':coordinates', (coordinates) => {
      client.coordinates = coordinates;
    });

    client.receive(this.name + ':restart', (coordinates) => {
      client.coordinates = coordinates;
    });
  }
}

module.exports = ServerLocator;

'use strict';

const EventEmitter = require('events').EventEmitter; // TODO: remove EventEmitter? (Implement our own listeners)

/**
 * The {@link ServerModule} base class is used to create a *Soundworks* module on the server side.
 * Each module should have a {@link ServerModule#connect} and a {@link ServerModule#disconnect} method.
 * Any module mapped to the type of client `clientType` (thanks to the {@link server#map} method) would call its {@link ServerModule#connect} method when such a client connects to the server, and its {@link ServerModule#disconnect} method when such a client disconnects from the server.
 * @example
 * class MyModule extends serverSide.Module {
 *   constructor('my-module-name') {
 *     ... // anything the constructor needs
 *   }
 *
 *   connect(client) {
 *     ... // what the module has to do when a client connects to the server
 *   }
 *
 *   disconnect(client) {
 *     ... // what the module has to do when a client disconnects from the server
 *   }
 * }
 */
export default class ServerModule extends EventEmitter {
  /**
    * Creates an instance of the class.
    * @param {Object} [options={}] The options.
    * @param {string} [options.name='unnamed'] The name of the module.
   */
  constructor(name) {
    super();

    /**
     * The name of the module.
     * @type {string}
     */
    this.name = name;
  }

  /**
   * Called when the `client` connects to the server.
   * This method should handle the logic of the module on the server side. For instance, it can take care of the communication with the client side module by setting up WebSocket message listeners and sending WebSocket messages, or it can add the client to a list to keep track of all the connected clients.
   * @param {ServerClient} client The connected client.
   */
  connect(client) {
    // Setup an object
    client.modules[this.name] = {};
  }

  /**
   * Called when the client `client` disconnects from the server.
   * This method should handle the logic when that happens. For instance, it can remove the socket message listeners, or remove the client from the list that keeps track of the connected clients.
   * @param {ServerClient} client The disconnected client.
   */
  disconnect(client) {
    // delete client.modules[this.name] // TODO?
  }
}

